  @Override
  public void updateFlathubInfo(boolean forceUpdate) {

    Optional<AppstreamUpdateInfo> updateInfo;

    try {

      updateInfo = getAppStreamUpdateInfo();

      if(updateInfo.isPresent()){

        LOGGER.info("Searching updates for " + updateInfo.get().getRepoName() + " from appstream...");

        FlatpakRepo repo = apiService.findRepoByName(updateInfo.get().getRepoName());
        if (repo == null) {
          addFlathubRepo();
          repo = apiService.findRepoByName(updateInfo.get().getRepoName());
        }

        if (forceUpdate || (updateInfo.get().isUpdatesAvailable() && !updateInfo.get().getCommit()
          .equalsIgnoreCase(repo.getCurrentOstreeCommit()))) {
          updateRepoInfo(repo, updateInfo.get());
        } else {
          LOGGER.info(repo.getName() + " is already up to date");
        }

      }
      else{
        LOGGER.error("Couldn't read any appstream information file. The script appstream-extractor might not be properly running");
      }



    } catch (IOException e) {
      LOGGER.error("Error updating appstream info for repo", e);
    }

  }


  private Optional<AppstreamUpdateInfo> getAppStreamUpdateInfo() throws IOException {

    File extractorInfoFile = new File(flathubAppStreamExtractorInfoFile);

    if (!extractorInfoFile.exists()) {
      extractorInfoFile = new File(flathubAppStreamExtractorInfoLegacyFile);
    }

    if (extractorInfoFile.exists()) {

      AppstreamUpdateInfo info = new AppstreamUpdateInfo();

      String line;

      //Use try-with-resource to get auto-closeable reader instance
      try (BufferedReader bufferedReader = new BufferedReader(new FileReader(extractorInfoFile))) {

        // use the readLine method of the BufferedReader to read one line at a time.
        // the readLine method returns null when there is nothing else to read.
        while ((line = bufferedReader.readLine()) != null) {
          if (line.startsWith("REPO=")) {
            info.setRepoName(line.replace("REPO=", ""));
          } else if (line.startsWith("EXPORT_DATA=")) {
            info.setExportDataPath(line.replace("EXPORT_DATA=", ""));
          } else if (line.startsWith("COMMIT=")) {
            info.setCommit(line.replace("COMMIT=", ""));
          } else if (line.startsWith("DATE=")) {
            //TODO: set date
            //info.setD
          }

        }

        File exportDataFolder = new File(info.getExportDataPath());
        
        if (exportDataFolder.exists() && exportDataFolder.isDirectory()) {
          info.setUpdatesAvailable(true);
        }
        else{
          info.setUpdatesAvailable(false);
        }

        return Optional.ofNullable(info);

      }

    }

    return Optional.empty();
  }


  private void updateRepoInfo(FlatpakRepo repo, AppstreamUpdateInfo appstreamInfo) {

    Map<String, AppdataComponent> incompleteComponentsMap = new HashMap<>();
    AppdataComponent previousIncompleteComponentWithSameFlatpakId;

    List<AppdataComponent> componentList = null;

    LOGGER.info("Updating repo info for " + repo.getName());

    try {

      componentList = appstreamInfo.getAppData();

    } catch (JAXBException e) {
      LOGGER.error("Error while parsing appdata for repo " + repo.getName(), e);
    }

    if (componentList != null) {

      for (AppdataComponent component : componentList) {

        try {

          if (component.getFlatpakId() != null &&
            APPSTREAM_TYPE_DESKTOP.equalsIgnoreCase(component.getType()) &&
            "org.audacityteam.Audacity".equalsIgnoreCase(component.getFlatpakId())) {



              previousIncompleteComponentWithSameFlatpakId = incompleteComponentsMap
                .get(component.getFlatpakId());
              if (previousIncompleteComponentWithSameFlatpakId != null) {
                component.merge(previousIncompleteComponentWithSameFlatpakId);
                incompleteComponentsMap.remove(component.getFlatpakId());
              }

              if (this.validateAppdataInformation(component)) {
                updateAppInfo(repo, appstreamInfo, component);
              } else {
                incompleteComponentsMap.put(component.getFlatpakId(), component);
                LOGGER.debug("Adding " + component.getFlatpakId()
                  + " to the incomplete components list because it has incomplete or invalid appdata");
              }

          }

        } catch (Exception e) {
          LOGGER.error("Error updating info for app " + component.getFlatpakId(), e);
        }
      }

      Iterator it = incompleteComponentsMap.entrySet().iterator();
      while (it.hasNext()) {
        Map.Entry pair = (Map.Entry) it.next();
        LOGGER.warn(
          pair.getKey() + " has incomplete appdata and won't be added/updated in the webstore");
        it.remove(); // avoids a ConcurrentModificationException
      }

    }

    //Once imported delete the appdata folder
    // exportDataFolder.delete();

    repo.setCurrentOstreeCommit(appstreamInfo.getCommit());
    apiService.updateFlatpakRepo(repo);


  }

  private void updateAppInfo(FlatpakRepo repo, AppstreamUpdateInfo appstreamInfo,
    AppdataComponent component) {

    boolean appDataIsIncomplete = false;
    boolean isNewApp;
    App app = apiService.findAppByFlatpakAppId(component.getFlatpakId());

    if (app == null) {
      app = new App();
      isNewApp = true;
    } else {
      isNewApp = false;
    }

    app.setFlatpakAppId(component.getFlatpakId());
    app.setName(component.findDefaultName());
    app.setSummary(component.findDefaultSummary());

    app.setHomepageUrl(component.findHomepageUrl().orElse(""));
    app.setDonationUrl(component.findDonationUrl().orElse(""));
    app.setHelpUrl(component.findHelpUrl().orElse(""));
    app.setBugtrackerUrl(component.findBugtrackerUrl().orElse(""));
    app.setTranslateUrl(component.findTranslateUrl().orElse(""));
    app.setDeveloperName(component.findDefaultDeveloperName());

    app.setProjectLicense(component.getProjectLicense());
    app.setDescription(getDescription(component));
    setReleaseInfo(app, component, isNewApp);
    importIcons(app, appstreamInfo, component, copyAppstreamIconsToServer);

    //TODO:
    //Keywords (translatable)
    //kudos
    //Languages (percentage)
    //<bundle type="flatpak" runtime="org.gnome.Platform/x86_64/3.22" sdk="org.gnome.Sdk/x86_64/3.22">app/org.gnome.Weather/x86_64/stable</bundle>

    app.setFlatpakRepo(repo);
    apiService.updateApp(app);

    importScreenshots(app, component);
    importCategories(app, component);

    if (generateFlatpakref) {
      generateFlatpakrefFile(app);
    }

  }

  /**
   * Check if the component contains the required appdata:
   * - For all components: FlatpakId, Name, Summary
   * - For apps require also: Description, Icon
   *
   * Check data size limits:
   * - description.length < App.APP_DESCRIPTION_LENGTH
   *
   * @return true if the component has the required info for its type and respects the size limits
   */
  private boolean validateAppdataInformation(AppdataComponent component) {

    boolean appDataIsValid = true;

    appDataIsValid = appDataIsValid && component.getFlatpakId() != null && !""
      .equalsIgnoreCase(component.getFlatpakId());

    appDataIsValid = appDataIsValid && component.findDefaultName() != null && !""
      .equalsIgnoreCase(component.findDefaultName());

    appDataIsValid = appDataIsValid && component.findDefaultSummary() != null && !""
      .equalsIgnoreCase(component.findDefaultSummary());

    if (APPSTREAM_TYPE_DESKTOP.equalsIgnoreCase(component.getType())) {

      //appDataIsValid = appDataIsValid && component.findDefaultDescription() != null && !"".equalsIgnoreCase(component.findDefaultDescription());
      if (component.findDefaultDescription() != null) {
        appDataIsValid = appDataIsValid
          && component.findDefaultDescription().length() < App.APP_DESCRIPTION_LENGTH;
      }

      String hiDpiIconUrl = component.findIconUrl(ICON_BASE_RELATIVE_PATH, ICON_HEIGHT_HIDPI);
      String defaultIconUrl = component.findIconUrl(ICON_BASE_RELATIVE_PATH, ICON_HEIGHT_DEFAULT);
      appDataIsValid = appDataIsValid && (!"".equalsIgnoreCase(hiDpiIconUrl) || !""
        .equalsIgnoreCase(defaultIconUrl));
    }

    if (!appDataIsValid) {
      LOGGER.info(component.getFlatpakId());
    }

    return appDataIsValid;
  }

  private void generateFlatpakrefFile(App app) {

    File destFlatpakRefFile = new File(flatpakrefDestPath + File.separator +
      app.getFlatpakAppId() + ".flatpakref");

    if (destFlatpakRefFile.getParentFile() != null) {
      destFlatpakRefFile.getParentFile().mkdirs();
    }

    if (!destFlatpakRefFile.exists()) {
      try {
        FlatpakRefFileCreator.createFlatpakRefFile(
          destFlatpakRefFile.getPath(), app);
      } catch (IOException e) {
        e.printStackTrace();
      }
    }

  }

  private void setReleaseInfo(App app, AppdataComponent component, boolean isNewApp) {

    Optional<ReleaseInfo> releaseInfo = component.findReleaseInfoByMostRecentAndLangIsDefault();
    OffsetDateTime releaseDate;
    String releaseVersion = "";
    String releaseDescription = "";

    if (isNewApp) {
      app.setInStoreSinceDate(OffsetDateTime.now());
    }

    if (releaseInfo.isPresent()) {

      if (releaseInfo.get().getTimestamp() != 0) {
        Instant instant = Instant.ofEpochSecond(releaseInfo.get().getTimestamp());
        releaseDate = instant.atOffset(ZoneOffset.UTC);
      } else {
        releaseDate = OffsetDateTime.now();
      }

      if (releaseInfo.get().getVersion() != null) {
        releaseVersion = releaseInfo.get().getVersion();
      }

      if (releaseInfo.get().getDescription() != null
        && releaseInfo.get().getDescription().length() < App.APP_RELEASE_DESCRIPTION_LENGTH) {
        releaseDescription = releaseInfo.get().getDescription();
      }

      app.setCurrentReleaseDate(releaseDate);

    }

    app.setCurrentReleaseVersion(releaseVersion);
    app.setCurrentReleaseDescription(releaseDescription);


  }

  private String getDescription(AppdataComponent component) {

    if ("".equalsIgnoreCase(component.findDefaultDescription())) {
      return MSG_DESCRIPTION_NOT_AVAILABLE;
    } else {
      return component.findDefaultDescription();
    }

  }


