  private void importScreenshots(App app, AppdataComponent component) {

    LOGGER.info("Screenshots for " + app.getFlatpakAppId());

    if (component.getScreenshotsByLangDefault() != null
      && component.getScreenshotsByLangDefault().size() > 0) {

      //Remove existing screenshots in the database
      app.getScreenshots().clear();
      apiService.deleteScrenshotsByApp(app);

      for (org.freedesktop.appstream.ScreenshotInfo appStreamScreenshotInfo : component
        .getScreenshotsByLangDefault()) {

        Screenshot screenshot = new Screenshot();

        screenshot.setThumbUrl(
          appStreamScreenshotInfo.findThumbnailUrlByHeightAndLangDefault(SCREENSHOT_HEIGHT_THUMBNAIL).orElse(""));
        screenshot.setImgMobileUrl(
          appStreamScreenshotInfo.findThumbnailUrlByHeightAndLangDefault(SCREENSHOT_HEIGHT_MOBILE).orElse(""));
        screenshot.setImgDesktopUrl(
          appStreamScreenshotInfo.findThumbnailUrlByHeightAndLangDefault(SCREENSHOT_HEIGHT_DESKTOP).orElse(""));

        screenshot.setApp(app);
        app.addScreenshot(screenshot);
        apiService.updateScreenshot(screenshot);
      }

    }

  }


  private void importCategories(App app, AppdataComponent component) {

    if (component.getCategories() != null
      && component.getCategories().getCategory() != null) {
      for (String categoryName : component.getCategories().getCategory()) {
        if (!"".equalsIgnoreCase(categoryName)) {

          Category category = apiService.findCategoryByName(categoryName);
          if (category == null) {
            category = new Category(categoryName);
            apiService.updateCategory(category);
          }
          app.addCategory(category);
        }
      }
      apiService.updateApp(app);
    }

  }


  private void importIcons(App app, AppstreamUpdateInfo appstreamInfo, AppdataComponent component,
    boolean copyAppstreamIconsToServer) {

    String hiDpiIconUrl = component.findIconUrl(ICON_BASE_RELATIVE_PATH, ICON_HEIGHT_HIDPI);
    String defaultIconUrl = component.findIconUrl(ICON_BASE_RELATIVE_PATH, ICON_HEIGHT_DEFAULT);

    if (!"".equalsIgnoreCase(hiDpiIconUrl)) {
      app.setIconDesktopUrl(hiDpiIconUrl);
    } else {
      if (!"".equalsIgnoreCase(defaultIconUrl)) {
        app.setIconDesktopUrl(defaultIconUrl);
      }
    }

    if (!"".equalsIgnoreCase(defaultIconUrl)) {
      app.setIconMobileUrl(defaultIconUrl);
    } else {
      if (!"".equalsIgnoreCase(hiDpiIconUrl)) {
        app.setIconMobileUrl(hiDpiIconUrl);
      }
    }

    if (copyAppstreamIconsToServer) {
      copyAppstreamIconToServer(app, appstreamInfo, component, ICON_HEIGHT_HIDPI);
      copyAppstreamIconToServer(app, appstreamInfo, component, ICON_HEIGHT_DEFAULT);
    }

  }


  private void copyAppstreamIconToServer(App app, AppstreamUpdateInfo appstreamInfo,
    AppdataComponent component, short height) {

    Icon icon = component.findIconByHeight(height);

    if (icon != null && ICON_TYPE_CACHED.equalsIgnoreCase(icon.getType())) {

      String appstreamIconPath;

      if (icon.getValue().contains(icon.getHeight() + "x" + icon.getWidth())) {
        appstreamIconPath = appstreamInfo.getExportDataPath() + File.separator + "icons" +
          File.separator + icon.getValue();
      } else {
        appstreamIconPath = appstreamInfo.getExportDataPath() + File.separator + "icons" +
          File.separator + icon.getHeight() + "x" + icon.getWidth() +
          File.separator + icon.getValue();
      }

      File appstreamIconFile = new File(appstreamIconPath);

      try {

        if (appstreamIconFile.isFile()) {

          File destIconPath = new File(
            iconDestPath + File.separator + height + "x" + height + File.separator + icon
              .getValue());

          if (destIconPath.getParentFile() != null) {
            destIconPath.getParentFile().mkdirs();
          }

          if (!destIconPath.exists()) {
            java.nio.file.Files.copy(appstreamIconFile.toPath(), destIconPath.toPath());
          }
        } else {
          LOGGER.info("Icon for " + app.getFlatpakAppId() + " not available");
        }
      } catch (IOException e) {
        LOGGER.error("Error copying icon", e);
      }
    } else {
      LOGGER.info("Icon for " + app.getFlatpakAppId() + " not available");
    }

  }

  private void importIcons2(App app, AppstreamUpdateInfo appstreamInfo, AppdataComponent component,
    boolean copyAppstreamIconsToServer) {

    Icon icon = component.findIconByHeight(ICON_HEIGHT_HIDPI);
    if (icon == null) {
      icon = component.findIconByHeight(ICON_HEIGHT_DEFAULT);
    }
    if (icon != null) {

      String iconPath;

      if (icon.getValue().contains(icon.getHeight() + "x" + icon.getWidth())) {
        iconPath = appstreamInfo.getExportDataPath() + File.separator + "icons" +
          File.separator + icon.getValue();
      } else {
        iconPath = appstreamInfo.getExportDataPath() + File.separator + "icons" +
          File.separator + icon.getHeight() + "x" + icon.getWidth() +
          File.separator + icon.getValue();
      }

      File iconFile = new File(iconPath);

      try {

        if (iconFile.isFile()) {

          File destIconPath = new File(
            iconDestPath + File.separator + app.getFlatpakAppId() + ".png");

          if (!destIconPath.exists()) {
            java.nio.file.Files.copy(iconFile.toPath(), destIconPath.toPath());
          }
        } else {
          LOGGER.info("Icon for " + app.getFlatpakAppId() + " not available");
        }
      } catch (IOException e) {
        LOGGER.error("Error copying icon", e);
      }
    } else {
      LOGGER.info("Icon for " + app.getFlatpakAppId() + " not available");
    }

  }


